"""
Platform Decorators
===================

1. linux

Usage:

>>> @linux
... def myfunction():
...     pass

"""

import logging
import platform

LOGGER = logging.getLogger(__name__)
LOGGER.setLevel(logging.INFO)
SYSTEM = platform.system()
LOGGER.debug(SYSTEM)

class UnsupportedError(Exception):
    pass

def linux(func):
    def function_wrapper():
        if SYSTEM != "Linux":
            raise UnsupportedError(SYSTEM)
        func()
    return function_wrapper