from lxk_testlib.panelnav.core.enum import Action
from lxk_testlib.panelnav.navigation import  PanelNavigation

navigation = PanelNavigation()
navigation.initialize(
    panelsize="panel28",
    printerip="10.194.7.16",
    is_mfp=True,
    is_color=True,
    firmware="CXLBL.07S.024"
)
navigation.run(Action.PRESS_KEY, "KEYCODE_HOME", locator=None)
navigation.run(Action.WAIT_UNTIL_FOUND, locator="Copy")
navigation.run(Action.PRESS, locator="Copy")
navigation.run(Action.WAIT_UNTIL_FOUND, locator="ID Copy")
navigation.run(Action.PRESS, locator="Color Balance")
