""" Interfaces ``actions`` sub-package using get_actions() function.
"""

from . import (
    press,
    select,
    verify,
    keyboard,
    wait,
    return_
)
from lxk_testlib.panelnav.core.models.actions import (
    ActionBuilder,
    ActionFactory
)
from lxk_testlib.panelnav.core.enum import Action, PanelSize

def get_actions():
    """ Returns a factory of UPS actions with different implementations based
    on panelsize (i.e. panel2L, panel24, panel28, panel43, etc.)
    """
    # create factory names
    factory = ActionFactory()
    factory.add_action(Action.PRESS,                    ActionBuilder())
    factory.add_action(Action.SELECT,                   ActionBuilder())
    factory.add_action(Action.VERIFY,                   ActionBuilder())
    factory.add_action(Action.VERIFY_WIDGET_FOUND,      ActionBuilder())
    factory.add_action(Action.VERIFY_WIDGET_NOT_FOUND,  ActionBuilder())
    factory.add_action(Action.VERIFY_WIDGET_COUNT,      ActionBuilder())
    factory.add_action(Action.TYPE,                     ActionBuilder())
    factory.add_action(Action.PRESS_KEY,                ActionBuilder())
    factory.add_action(Action.PRESS_KEY_COMBINATION,    ActionBuilder())
    factory.add_action(Action.CLEAR_TEXT,               ActionBuilder())
    factory.add_action(Action.WAIT_UNTIL_FOUND,         ActionBuilder())
    factory.add_action(Action.WAIT_UNTIL_NOT_FOUND,     ActionBuilder())
    factory.add_action(Action.RETURN_NODE_OR_ATTR,      ActionBuilder())

    # register action implementations
    action = factory.get_action(Action.PRESS)
    action.add_panelsize(PanelSize.PANEL10, press.TouchPress())
    action.add_panelsize(PanelSize.PANEL7,  press.TouchPress())
    action.add_panelsize(PanelSize.PANEL43, press.TouchPress())
    action.add_panelsize(PanelSize.PANEL28, press.TouchPress())
    action.add_panelsize(PanelSize.PANEL24, press.NonTouchVSelect())
    action.add_panelsize(PanelSize.PANEL2L, press.NonTouchHSelect())

    action = factory.get_action(Action.SELECT)
    action.add_panelsize(PanelSize.PANEL10, select.GenericPresetSelect())
    action.add_panelsize(PanelSize.PANEL7,  select.GenericPresetSelect())
    action.add_panelsize(PanelSize.PANEL43, select.GenericPresetSelect())
    action.add_panelsize(PanelSize.PANEL28, select.GenericPresetSelect())
    action.add_panelsize(PanelSize.PANEL24, select.GenericPresetSelect())
    action.add_panelsize(PanelSize.PANEL2L, select.GenericPresetSelect())

    action = factory.get_action(Action.VERIFY)
    action.add_panelsize(PanelSize.PANEL10, verify.GenericVerify())
    action.add_panelsize(PanelSize.PANEL7,  verify.GenericVerify())
    action.add_panelsize(PanelSize.PANEL43, verify.GenericVerify())
    action.add_panelsize(PanelSize.PANEL28, verify.GenericVerify())
    action.add_panelsize(PanelSize.PANEL24, verify.GenericVerify())
    action.add_panelsize(PanelSize.PANEL2L, verify.GenericVerify())

    action = factory.get_action(Action.VERIFY_WIDGET_FOUND)
    action.add_panelsize(PanelSize.PANEL10, verify.AndroidVerifyWidgetFound())
    action.add_panelsize(PanelSize.PANEL7,  verify.AndroidVerifyWidgetFound())
    action.add_panelsize(PanelSize.PANEL43, verify.AndroidVerifyWidgetFound())
    action.add_panelsize(PanelSize.PANEL28, verify.SmallTouchVerifyWidgetFound())
    action.add_panelsize(PanelSize.PANEL24, verify.NonTouchVerifyWidgetFound())
    action.add_panelsize(PanelSize.PANEL2L, verify.NonTouchVerifyWidgetFound())

    action = factory.get_action(Action.VERIFY_WIDGET_NOT_FOUND)
    action.add_panelsize(PanelSize.PANEL10, verify.AndroidVerifyWidgetNotFound())
    action.add_panelsize(PanelSize.PANEL7,  verify.AndroidVerifyWidgetNotFound())
    action.add_panelsize(PanelSize.PANEL43, verify.AndroidVerifyWidgetNotFound())
    action.add_panelsize(PanelSize.PANEL28, verify.SmallTouchVerifyWidgetNotFound())
    action.add_panelsize(PanelSize.PANEL24, verify.NonTouchVerifyWidgetNotFound())
    action.add_panelsize(PanelSize.PANEL2L, verify.NonTouchVerifyWidgetNotFound())

    action = factory.get_action(Action.VERIFY_WIDGET_COUNT)
    action.add_panelsize(PanelSize.PANEL24, verify.NonTouchVerifyWidgetCount())
    action.add_panelsize(PanelSize.PANEL2L, verify.NonTouchVerifyWidgetCount())

    action = factory.get_action(Action.TYPE)
    action.add_panelsize(PanelSize.PANEL10, keyboard.GenericType())
    action.add_panelsize(PanelSize.PANEL7,  keyboard.GenericType())
    action.add_panelsize(PanelSize.PANEL43, keyboard.GenericType())
    action.add_panelsize(PanelSize.PANEL28, keyboard.GenericType())
    action.add_panelsize(PanelSize.PANEL24, keyboard.GenericType())
    action.add_panelsize(PanelSize.PANEL2L, keyboard.GenericType())

    action = factory.get_action(Action.PRESS_KEY)
    action.add_panelsize(PanelSize.PANEL10, keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL7,  keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL43, keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL28, keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL24, keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL2L, keyboard.GenericPressKey())

    action = factory.get_action(Action.PRESS_KEY_COMBINATION)
    action.add_panelsize(PanelSize.PANEL10, keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL7,  keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL43, keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL28, keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL24, keyboard.GenericPressKey())
    action.add_panelsize(PanelSize.PANEL2L, keyboard.GenericPressKey())

    action = factory.get_action(Action.CLEAR_TEXT)
    action.add_panelsize(PanelSize.PANEL10, keyboard.ClearText())
    action.add_panelsize(PanelSize.PANEL7,  keyboard.ClearText())
    action.add_panelsize(PanelSize.PANEL43, keyboard.ClearText())
    action.add_panelsize(PanelSize.PANEL28, keyboard.ClearText())
    action.add_panelsize(PanelSize.PANEL2L, keyboard.ClearText())

    action = factory.get_action(Action.WAIT_UNTIL_FOUND)
    action.add_panelsize(PanelSize.PANEL10, wait.GenericWaitUntilFound())
    action.add_panelsize(PanelSize.PANEL7,  wait.GenericWaitUntilFound())
    action.add_panelsize(PanelSize.PANEL43, wait.GenericWaitUntilFound())
    action.add_panelsize(PanelSize.PANEL28, wait.GenericWaitUntilFound())
    action.add_panelsize(PanelSize.PANEL24, wait.GenericWaitUntilFound())
    action.add_panelsize(PanelSize.PANEL2L, wait.GenericWaitUntilFound())

    action = factory.get_action(Action.WAIT_UNTIL_NOT_FOUND)
    action.add_panelsize(PanelSize.PANEL10, wait.GenericWaitUntilNotFound())
    action.add_panelsize(PanelSize.PANEL7,  wait.GenericWaitUntilNotFound())
    action.add_panelsize(PanelSize.PANEL43, wait.GenericWaitUntilNotFound())
    action.add_panelsize(PanelSize.PANEL28, wait.GenericWaitUntilNotFound())
    action.add_panelsize(PanelSize.PANEL24, wait.GenericWaitUntilNotFound())
    action.add_panelsize(PanelSize.PANEL2L, wait.GenericWaitUntilNotFound())

    action = factory.get_action(Action.RETURN_NODE_OR_ATTR)
    action.add_panelsize(PanelSize.PANEL10, return_.GenericReturn())
    action.add_panelsize(PanelSize.PANEL7,  return_.GenericReturn())
    action.add_panelsize(PanelSize.PANEL43, return_.GenericReturn())
    action.add_panelsize(PanelSize.PANEL28, return_.GenericReturn())
    action.add_panelsize(PanelSize.PANEL24, return_.GenericReturn())
    action.add_panelsize(PanelSize.PANEL2L, return_.GenericReturn())

    return factory
