""" Constants
"""

from enum import Enum

class PanelSize(Enum):

    PANEL10 = "panel10"
    PANEL7 = "panel7"
    PANEL43 = "panel43"
    PANEL28 = "panel28"
    PANEL24 = "panel24"
    PANEL2L = "panel2L"

    @classmethod
    def items(cls):
        return [member.value for _, member in PanelSize.__members__.items()]

class Action(Enum):

    PRESS = "press"
    SELECT = "select"
    VERIFY = "verify"
    VERIFY_WIDGET_FOUND = "verify widget found"
    VERIFY_WIDGET_NOT_FOUND = "verify widget not found"
    VERIFY_WIDGET_COUNT = "verify widget count"
    TYPE = "type"
    CLEAR_TEXT = "clear text"
    PRESS_KEY = "press key"
    PRESS_KEY_COMBINATION = "press key combination" # non-UPS special action
    WAIT_UNTIL_FOUND = "wait until found"
    WAIT_UNTIL_NOT_FOUND = "wait until not found"
    RETURN_NODE_OR_ATTR = "return"

class Filter(Enum):

    # Printer Function Types
    SFP = "SFP"
    MFP = "MFP"
