""" home.shortcutcenter
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen
from lxk_testlib.panelnav.locators.panel7.home import (
    copy, email, fax, ftp
)

screen = Screen()

locator = screen.add_locator("Screen Title",marker=True)
locator.widget = "resource-id='android:id/action_bar_title'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Menu List Item")
locator.widget = "text='%s' && resource-id='esf.shortcutCenterUi:id/tvRowText'"

locator = screen.add_locator("Copy Shortcut")
locator.widget = "resource-id='esf.shortcutCenterUi:id/row_tvShortcutName'"
locator.text = "%s"
locator.next_screen = copy
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Fax Shortcut")
locator.widget = "resource-id='esf.shortcutCenterUi:id/row_tvShortcutName'"
locator.text = "%s"
locator.next_screen = fax
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Email Shortcut")
locator.widget = "resource-id='esf.shortcutCenterUi:id/row_tvShortcutName'"
locator.text = "%s"
locator.next_screen = email
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("FTP Shortcut")
locator.widget = "resource-id='esf.shortcutCenterUi:id/row_tvShortcutName'"
locator.text = "%s"
locator.next_screen = ftp
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Create Shortcut Button")
locator.widget = "text='Create Shortcut' && resource-id='esf.shortcutCenterUi:id/tvCreateShortcut'"

locator = screen.add_locator("Shortcut Overflow Button")
locator.widget = "resource-id='esf.shortcutCenterUi:id/row_btnShortcutOption' && class='android.widget.Button'"
locator.text = "%s"

locator = screen.add_locator("Shortcut Overflow Item")
locator.area = "class='android.widget.ListView'"
locator.widget = "text='%s' && resource-id='android:id/title'"

locator = screen.add_locator("Edit Dialog Button")
locator.widget = "text='%s' && class='android.widget.Button'"

locator = screen.add_locator("Edit Dialog Title")
locator.widget = "text='%s' && class='android.widget.TextView' && resource-id='esf.shortcutCenterUi:id/txtDialogTitle'"

locator = screen.add_locator("Edit Dialog Message")
locator.widget = "text='%s' && class='android.widget.TextView' && resource-id='esf.shortcutCenterUi:id/txtDialogMsg'"

locator = screen.add_locator("Edit Dialog Input")
locator.widget = "text='%s' && class='android.widget.EditText'"
locator.add_action(
     action=Action.VERIFY,
     specifier="verify text='%s'"
)

