""" panel24.home.changelanguage
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

locator = screen.add_locator("Popup List Title", marker=True)
locator.area = "label"
locator.widget = "type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Popup Message")
locator.widget = "viewname='TextPopupView' && type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Popup List Item")
locator.area = "list"
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)