from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

locator = screen.add_locator("Screen Title", marker=True)
locator.widget = "text='Fax'"

#Fax To, Color, Content Type, Content Source, Transmission Log Action
#Original Size, Orientation, Resolution, Darkness, Scan Edge to edge
#Shadow Detail, Background Removal, Sharpness, Contrast, Negative Image
#Mirror Image, Send Time
locator = screen.add_locator("Menu List Item")
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify settingValue='%s'"
)

locator = screen.add_locator("Option Value")
locator.area = "list:index=2"
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
    action=Action.SELECT,
    specifier="h-select"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

#Fax To
locator = screen.add_locator("Keyboard Edit")
locator.widget = "type='EditBox'"
locator.text = "%s"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#Darkness, Shadow Detail, Sharpness, Contrast
locator = screen.add_locator("Slider Value")
locator.area = "list:index=2"
locator.widget = "selected='true' && type='ListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify settingValue='%s'"
)
locator.add_action(
    action=Action.SELECT,
    specifier="h-select:data_type=int:set settingValue='%s'"
)
