""" home.shortcuts
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen
from lxk_testlib.panelnav.locators.panel24.home import copy

screen = Screen()


locator = screen.add_locator("Screen Title", marker=True)
locator.widget = "type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Menu List Item")
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
     action=Action.VERIFY,
     specifier="verify settingValue='%s'"
)

locator = screen.add_locator("Copy Shortcut Item")
locator.widget = "text='%s' && type='ListItem'"
locator.next_screen = copy

locator = screen.add_locator("Shortcut Item")
locator.area = "list"
locator.widget = "text='%s' && type='ListItem'"

locator = screen.add_locator("Slider Value")
locator.area = "list:index=2"
locator.widget = "selected='true' && type='ListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify settingValue='%s'"
)
locator.add_action(
    action=Action.SELECT,
    specifier="h-select:data_type=int:set settingValue='%s'"
)

locator = screen.add_locator("Option Value")
locator.area = "list:index=2"
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
    action=Action.SELECT,
    specifier="h-select"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

locator = screen.add_locator("Keyboard Edit")
locator.widget = "type='EditBox'"
locator.text = "%s"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Setting Value")
locator.area = "list:index=2"
locator.widget = "type='ListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify settingValue='%s'"
)
locator.add_action(
    action=Action.SELECT,
    specifier="h-select:data_type=int:set settingValue='%s'"
)

locator = screen.add_locator("Copy To Value")
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
    action=Action.SELECT,
    specifier="h-select"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

locator = screen.add_locator("Choose An Action")
locator.widget = "text='%s' && type='Label'"
locator.add_action(
    action=Action.PRESS,
    specifier="h-select:option_count=2"
)

locator = screen.add_locator("Message")
locator.widget = "text='%s' && type='Label'"