""" panel24.home.statussupplies
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

#--------------------------------------------------------------------#
#STATUS/SUPPLIES SCREEN LOCATORS - OPTIMIZED/MINIMIZED VERSION
#--------------------------------------------------------------------#

locator = screen.add_locator("Screen Title", marker=True)
locator.widget = "type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#--------------------------------------------------------------------#
# Menu that uses this locator:
# main screen= Trays, Alerts, Supplies,
# alerts= Warnings*,
# trays= Tray 1, Manual Paper, Manual Envelope,
# supplies= Black Cartridge, Cyan Cartridge, Magenta Cartridge, Yellow Cartridge, Waste Toner Bottle
#--------------------------------------------------------------------#

locator = screen.add_locator("Menu List Item")
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Menu List Item Value")
locator.text = "%s"
locator.widget = "textid!='' && type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#--------------------------------------------------------------------#
# Menu that uses this locator: trays= Type, Size
#--------------------------------------------------------------------#
locator = screen.add_locator("Menu List Item Label")
locator.text = "%s"
locator.widget = "type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#--------------------------------------------------------------------#
# Menu that uses this locator: All Popup Dialog
#--------------------------------------------------------------------#

locator = screen.add_locator("Popup Dialog Message")
locator.widget = "type='Label'"
locator.text = "%s"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Popup Dialog Button")
locator.widget = "text='%s' && (type='ListItem' or type='Label')"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)