""" panel24.home.usbdrive
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()
locator = screen.add_locator("Landing Screen", marker=True)
locator.area = "list"
locator.widget = "text='Scan to USB' && type='ListItem'"

#Menu List Item
#Can also be used as pop up list
locator = screen.add_locator("Menu List Item")
locator.area = "list"
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify %s='%s'"
)
locator.add_action(
    action=Action.PRESS,
    specifier="v-select"
)

#Settings List Item
locator = screen.add_locator("Settings List Item")
locator.area = "list:index=1"
locator.widget = "text='%s' && type='ListItem'"

#Value List Item
locator = screen.add_locator("Value List Item")
locator.area = "list:index=2"
locator.widget = "text='%s' && type='ListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify %s='%s'"
)
locator.add_action(
    action=Action.PRESS,
    specifier="h-select"
)

#Value List Item Help Text
locator = screen.add_locator("Value List Item Help Text")
locator.area = "list:index=2"
locator.widget = "helpText='%s'"

#Integer ScrollView
locator = screen.add_locator("Integer ScrollView")
locator.area = "list:index=2"
locator.widget = "helpText='%s'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify %s='%s'"
)
locator.add_action(
    action=Action.SELECT,
    specifier="h-select:data_type=int:set settingValue='%s'"
)

#Dialog Message
locator = screen.add_locator("Dialog Message")
locator.widget = "text='%s' && type='Label'"

#Dialog Button
locator = screen.add_locator("Dialog Button")
locator.widget = "text='%s' && type='Label'"

# Scan the next page, Finish the job
locator = screen.add_locator("Choose An Action")
locator.widget = "text='%s' && type='Label'"
locator.add_action(
    action=Action.PRESS,
    specifier="h-select:option_count=2"
)