""" home.diagnostics
"""
from lxk_testlib.panelnav.core.enum import Action
from lxk_testlib.panelnav.core.models.screen import Screen

# Add screen and components below
screen = Screen()
#Verify Locator Exist       Screen Title       vtext=<screen_title>
locator = screen.add_locator("Screen Title", marker=True)
locator.widget = "x='0' && y='0' && type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#Press Locator              Menu List Item         vtext=<setting>
locator = screen.add_locator("Menu List Item")
locator.area = "ListWidget"
locator.widget = "text='%s' && type='Label'"

#Verify Locator Exist       Popup List Title       vtext=<list_title>
locator = screen.add_locator("Popup List Title")
locator.widget = "x='8' && y='9' && type='Label'"
locator.text = "%s"

#Press Locator                Popup List Value                         vtext=<value>
#Verify Locator Attribute     Popup List Value        <selected>       vtext=<value>
locator = screen.add_locator("Popup List Value")
locator.area = "ListWidget"
locator.text = "%s"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

#Verify Locator Exist       Popup Dialog Title       vtext=<dialog_title>
locator = screen.add_locator("Popup Dialog Title")
locator.widget = "x='8' && y='9' && type='Label'"
locator.text = "%s"

#Verify Locator Exist       Popup Dialog Message       vtext=<dialog_message>
locator = screen.add_locator("Popup Dialog Message")
locator.widget = "x='8' && y='36' && type='Label'"
locator.text = "%s"

### Buttons ##
#Press Locator       Button      vtext=OK
locator = screen.add_locator("Popup Dialog Button")
locator.widget = "text='%s' && type='QPushButton'"

## Keyboard ##
locator = screen.add_locator("Keyboard Edit")
locator.widget = "type='LineEdit'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)
locator = screen.add_locator("Numpad Button")
locator.widget = "text='%s' && type='ImageButton'"
locator = screen.add_locator("Keyboard Button")
locator.widget = "text='%s' && type='KeyboardButton'"
