"""home.fax
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

#--------------#
#Settings: Fax
#--------------#
locator = screen.add_locator("Screen Title", marker=True)
locator.widget = "x='0' && y='0' && type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#----------------------------------------------------#
#Settings: Shortcut Star Button, Shortcut Add Button
#----------------------------------------------------#
locator = screen.add_locator("Title Bar Button")
locator.widget = "name='titleBarRightButton' && type='ImageButton'"

#---------------------------#
#Settings: Fax Number Entry
#---------------------------#
locator = screen.add_locator("Keyboard Edit")
locator.widget = "type='LineEdit'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Numpad Button")
locator.widget = "text='%s' && type='ImageButton'"

#---------------------------------------------------#
#Settings: Color, Original Size, Sides, Flip Style,
#Resolution, Transmission Log, Scan Edge to Edge,
#Background Removal, Contrast, Color Balance,
#Clear Values
#---------------------------------------------------#
locator = screen.add_locator("Menu List Item")
locator.widget = "text='%s' && type='Label'"

#----------------------------------------------#
#Settings: Original Size List, Resolution List,
#Transmission Log List, Contrast List
#----------------------------------------------#
locator = screen.add_locator("Popup List Title")
locator.widget = "text='%s' && type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Popup List Value")
locator.area = "ListWidget"
locator.widget = "text='%s' && type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

#-------------------------------------------#
#Settings: Send Time Next, Send Time Cancel
#-------------------------------------------#
locator = screen.add_locator("Popup Dialog Button")
locator.widget = "text='%s' && type='QPushButton'"

#----------------------------------------#
#Settings: Sides Value, Flip Style Value,
#Orientation
#----------------------------------------#
locator = screen.add_locator("Current Value")
locator.widget = "type='QLabel'"
locator.text = '%s'
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#------------------------------------------------#
#Settings: Darkness, Background Removal, Contrast
#------------------------------------------------#
locator = screen.add_locator("ScrollUp Button")
locator.widget = "name='scrollUp' && type='ImageButton'"

locator = screen.add_locator("ScrollDown Button")
locator.widget = "name='scrollDown' && type='ImageButton'"


#----------------------------------#
#Settings: Month, Day & Year values
#----------------------------------#
locator = screen.add_locator("Manual Month")
locator.widget = "name='monthLabel'"
locator.add_action(
    action=Action.SELECT,
    specifier="select:data_type=int:forward_key=monthUp:backward_key=monthDown:set text='%s'"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Manual Day")
locator.widget = "name='dayLabel'"
locator.add_action(
    action=Action.SELECT,
    specifier="select:data_type=int:forward_key=dayUp:backward_key=dayDown:set text='%s'"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Manual Year")
locator.widget = "name='yearLabel'"
locator.add_action(
    action=Action.SELECT,
    specifier="select:data_type=int:forward_key=yearUp:backward_key=yearDown:set text='%s'"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#--------------------------------------------------------------------#
# Screen after scanning "CHOOSE AN ACTION" pop-up
# Three options for this: Cancel Job, Scan the next page, Finish the job
#--------------------------------------------------------------------#
locator = screen.add_locator("Choose An Action")
locator.widget = "text='%s' && type='QLabel'"

