""" home.copy
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

locator = screen.add_locator("Screen Title", marker=True)
locator.widget = "textid='0'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Menu List Item")
locator.widget = "text='%s' && type='DataObjectListItem'"

locator = screen.add_locator("Setting Value")
locator.area = "list"
locator.widget = "type='IntegerModel' or type='DarknessModel'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify value='%s'"
)
locator.add_action(
    action=Action.SELECT,
    specifier="h-select:data_type=int:set value='%s'"
)

locator = screen.add_locator("Option Value")
locator.area = "list"
locator.widget = "text='%s' && type='IntegerEnumListItem'"
locator.add_action(
    action=Action.SELECT,
    specifier="h-select"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

locator = screen.add_locator("Copy To Value")
locator.widget = "type='IntegerEnumListItem'"
locator.text = "%s"

locator = screen.add_locator("Status Label")
locator.widget = "type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#------------------------------------------------------------------------------
# Choose an action options
#------------------------------------------------------------------------------
locator = screen.add_locator("Choose An Action")
locator.widget = "text='%s' && type='StringListItem'"

# Scale
locator = screen.add_locator("Scale Value")
locator.area = "list"
locator.widget = "IntegerModel"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify value='%s'"
)