""" home.diagnostics
"""
from lxk_testlib.panelnav.core.enum import Action
from lxk_testlib.panelnav.core.models.screen import Screen

# Add screen and components below
screen = Screen()
#Verify Locator Exist       Screen Title       vtext=<screen_title>        
locator = screen.add_locator("Screen Title", marker=True)
locator.widget = "type='Label'"
locator.text = "%s"

#Verify Locator Exist       Menu List Item         <value>
#Press Locator              Menu List Item         vtext=<setting>
locator = screen.add_locator("Menu List Item")
locator.widget="text='%s' && type='StateNodeListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

#Verify Locator Attribute   List Option         <selected>      vtext=<value>
#Press Locator              List Option         vtext=<setting>
locator = screen.add_locator("List Option")
locator.widget = "text='%s' && type='IntegerEnumListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

#Set Locator Attribute        Menu List Value       <value>
#Verify Locator Attribute     Menu List Value       <value>  
locator = screen.add_locator("List Value")
locator.widget = "type='IntegerModel'"
locator.add_action(
    action=Action.SELECT,
    specifier="h-select:data_type=int:set value='%s'"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify value='%s'"
)
