""" 2Line_panel.home.heldjobs
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

locator = screen.add_locator("Screen Title", marker=True)
locator.widget = "type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Menu List Item")
locator.area = "list"
locator.widget = "text='%s' && type='DataObjectListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)

locator = screen.add_locator("Number Selector")
locator.widget = "type='IntegerModel'"
locator.add_action(
    action=Action.SELECT,
    specifier="h-select:data_type=int:set value='%s'"
)
locator.add_action(
    action=Action.VERIFY,
    specifier="verify value='%s'"
)

locator = screen.add_locator("Edit Box")
locator.widget = "type='TextEntry'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Message View String")
locator.widget = "text='%s' && type='Label'"

locator = screen.add_locator("Confirmation View String")
locator.widget = "type='Label'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Confirmation View List Item")
locator.area = "list"
locator.widget = "text='%s' && type='StringListItem'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify selected='%s'"
)