from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

#-----------------------------------------------------------------------------#
#Screen Title : App Profiles Optimize Locators
#-----------------------------------------------------------------------------#
locator = screen.add_locator("Screen Title", marker=True)
locator.area = "android:id/action_bar_container"
locator.widget = "resource-id='com.lxk.appProfiles:id/action_bar_title'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Drawer Navigation Button")
locator.widget = "resource-id='com.lxk.appProfiles:id/navControl' && class='android.widget.ImageView'"

# Menu List
