from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

#Main Screen
locator = screen.add_locator("Bookmarks Screen Title", marker=True)
locator.area = "android:id/action_bar_container"
locator.widget = "resource-id='com.lxk.JobQueue:id/action_bar_title'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

locator = screen.add_locator("Bookmarks Item Overflow")
locator.area = "resource-id='android:id/list'"
locator.widget = "resource-id='com.lxk.JobQueue:id/action' && class='android.widget.ImageView'"

locator = screen.add_locator("Bookmarks Item List")
locator.area = "resource-id='android:id/list'"
locator.widget = "text='%s' && resource-id='com.lxk.JobQueue:id/primary_text'"

locator = screen.add_locator("Bookmarks Overflow List")
locator.area = "android.widget.ListView:swipe_offsety0=1:swipe_offsety1=-2"
locator.widget = "text='%s' && resource-id='android:id/title'"

locator = screen.add_locator("Bookmarks Delete Screen Action")
locator.area = "resource-id='android:id/buttonPanel'"
locator.widget = "text='%s' && class='android.widget.Button'"