""" home.scanprofiles
"""
from lxk_testlib.panelnav.core.enum import Action, Filter
from lxk_testlib.panelnav.core.models.screen import Screen

screen = Screen()

#--------------------------------------------------------------------#
#E-MAIL SCREEN LOCATORS - OPTIMIZED/MINIMIZED VERSION
#--------------------------------------------------------------------#
locator = screen.add_locator("Screen Title", marker=True)
locator.area = "android:id/action_bar_container"
locator.widget = "resource-id='android:id/action_bar_title'"
locator.add_action(
    action=Action.VERIFY,
    specifier="verify text='%s'"
)

#--------------------------------------------------------------------#
# Settings that uses this locator: All, My Profiles and
# Scan to Computer
#--------------------------------------------------------------------#
locator = screen.add_locator("Menu List Item")
locator.area = "com.lxk.appProfiles:id/navigation"
locator.widget = "text='%s' && resource-id='com.lxk.appProfiles:id/menu_text' && class='android.widget.TextView'"

#--------------------------------------------------------------------#
# List of profiles
#--------------------------------------------------------------------#
locator = screen.add_locator("Profile Item")
locator.area = "android:id/list"
locator.widget = "text='%s' && class='android.widget.TextView'"

#--------------------------------------------------------------------#
# Delete profile button
#--------------------------------------------------------------------#
locator = screen.add_locator("Delete Profile Button")
locator.area = "com.lxk.appProfiles:id/profile_item"
locator.widget = "resource-id='com.lxk.appProfiles:id/action_name' && class='android.widget.ImageView'"
