import os
import logging
import socket
from lxk_testlib.utils import socket

LOGGER = logging.getLogger(__name__)
LOGGER.setLevel(logging.DEBUG)


def send_pjl_file(ip_address, file, wait_for_timeout=True, timeout=10, port=9100):
    """
    Get settings value of the printer using NPA ID.

    :Parameters:

        1. ip_address, ``<class 'str'>``
        2. file, ``<class 'str'>``, npa file containing an npa command

    :Returns: ``<class 'str'>``
    """
    
    if not os.path.exists(file):
        assert False, "Could not find " + file + "!!!"
        
    result = socket.send(ip_address, file, wait_for_timeout, timeout, port)
    return result
    