import os
import logging
import socket
from lxk_testlib import rob


LOGGER = logging.getLogger(__name__)
LOGGER.setLevel(logging.DEBUG)


def generate_mps_report(ip_address):
    """
    Generate MPS report

    :Parameters:

        1. ip_address, ``<class 'str'>``

    :Returns: ``<class 'str'>``
    """
    LOGGER.debug(ip_address)   
    
    cmd = '''call applications.mps generateReport "{s}" contentType "xml"'''
    LOGGER.debug(cmd)
    result = False
    return_output = rob.execute(ip_address, cmd)
    LOGGER.debug(return_output)
    if return_output[0]:
        result = return_output[1]        
    return result
    