"""
Rob Module
==========
"""

import os
import logging
from lxk_testlib.utils import ssh

LOGGER = logging.getLogger(__name__)
LOGGER.setLevel(logging.DEBUG)

def execute(ip_address, cmd):
    """
    SSH to ip_address and execute rob cmd.

    :Parameters:
        1. ip_address: Printer IPv4 Address
        2. cmd: rob command to be executed
        3. user: ssh username; defaults to printer default username
        4. pwd: ssh password; defaults to printer default password

    :Returns: ``None``

        1. Output from rob command through SSH.

    """
    LOGGER.debug(ip_address)
    LOGGER.debug(cmd)
    cmd = 'rob --bus {} {}'.format(ip_address, cmd)
    output = ssh.execute(ip_address, cmd)
    return output
 