from robot.api import logger


def assert_equal(actual, expected):
    """
    Verify values are equal
    """
    actual = str(actual)
    expected = str(expected)

    logger.info("Actual value: {}".format(actual))
    logger.info("Expected value: {}".format(expected))

    if actual != expected:
        raise AssertionError('Assertion failure, values are not equal.')

    return True
